import "./App.css";
import React, { useState } from "react";
import { UserInput } from "./UserInput";

function App() {
  const [toDo, setToDo] = useState([]);
  const [currentToDo, setCurrentToDo] = useState("");
  const [pos, setPos] = useState(0);
  const [background, setBackground] = useState("lightPink");
  const [checkedState, setCheckedState] = useState(
    new Array(toDo.length).fill(false)
  );

  const handleSubmit = (e) => {
    setToDo((prev) => [
      ...prev,
      {
        pos,
        background,
        id: Math.floor(Math.random() * 10000),
        value: currentToDo,
      },
    ]);
    e.preventDefault();
    setPos((prev) => prev + 1);
    setCurrentToDo("");
    setBackground(() => (pos % 2 === 0 ? "lightGreen" : "lightPink"));
  };

  const handleChange = ({ target }) => {
    setCurrentToDo(() => target.value);
  };

  const removeToDo = (id) => {
    setToDo((prev) => prev.filter((toRemove) => toRemove.id !== id));
  };

  const handleOnChange = (position) => {
    const updatedCheckedState = toDo.map((item, index) =>
      index === position ? !item : item
    );
    setCheckedState(updatedCheckedState);
  };

  return (
    <div className="container">
      <h1>Task</h1>
      <UserInput
        onSubmit={handleSubmit}
        toDo={toDo}
        currentToDo={currentToDo}
        onChange={handleChange}
      />
      <ul>
        {toDo.map((toDos, index) => (
          <li
            key={Math.floor(Math.random() * 10000)}
            id={toDos.id}
            style={{ backgroundColor: toDos.background }}
          >
            <input
              name="isChecked"
              type="checkbox"
              checked={checkedState[index]}
              onChange={() => handleOnChange(index)}
            />
            <span id="text">{toDos.value}</span>
            <span id="todo-span" onClick={() => removeToDo(toDos.id)}>
              ðŸ†‡
            </span>
          </li>
        ))}
      </ul>
      <StatBar toDo={toDo} />
    </div>
  );
}

const StatBar = ({ toDo }) => {
  return (
    <>
      <p>
        {toDo.length} {toDo.length !== 1 ? "items" : "item"} remaining
      </p>
    </>
  );
};

export default App;
