import "./App.css";
import React, { useState } from "react";

function App() {
  const [toDo, setToDo] = useState([{ id: 0, value: "Add a Todo!" }]);
  const [currentToDo, setCurrentToDo] = useState("");

  const handleSubmit = (e) => {
    setToDo((prev) => [...prev, currentToDo]);
    e.preventDefault();
    setCurrentToDo("");
  };

  const handleChange = ({ target }) => {
    setCurrentToDo(() => target.value);
  };

  const removeToDo = () => {
    setToDo(toDo.filter((toRemove) => toRemove.id !== toDo.id));
  };

  const randomBackground = [
    {
      backgroundColor: "lightGreen",
    },
    { backgroundColor: "lightPink" },
  ];
  return (
    <>
      <UserInput
        onSubmit={handleSubmit}
        toDo={toDo}
        onChange={handleChange}
        currentToDo={currentToDo}
      />
      <ul>
        {toDo.map((toDos) => (
          <li key={Math.floor(Math.random() * 10000)} id={toDos}>
            {toDos.value}{" "}
            <span id="todo-span" onClick={removeToDo}>
              ðŸ†‡
            </span>
          </li>
        ))}
      </ul>
    </>
  );
}

function UserInput({ onSubmit, onChange, currentToDo }) {
  return (
    <form onSubmit={onSubmit}>
      <input type="text" value={currentToDo} onChange={onChange} required />
      <button type="submit">submit</button>
    </form>
  );
}

export default App;
