import "./App.css";
import React, { useState } from "react";

function App() {
  const [toDo, setToDo] = useState(["Add a Todo!"]);
  const [currentToDo, setCurrentToDo] = useState("");

  const handleSubmit = (e) => {
    setToDo((prev) => [...prev, currentToDo]);
    e.preventDefault();
    setCurrentToDo("");
  };

  const handleChange = ({ target }) => {
    setCurrentToDo(() => target.value);
  };
  return (
    <>
      <UserInput
        onSubmit={handleSubmit}
        toDo={toDo}
        onChange={handleChange}
        currentToDo={currentToDo}
      />
      <ul>
        <ListOfToDos toDos={toDo} currentToDo={currentToDo} />
      </ul>
    </>
  );
}

function UserInput({ onSubmit, onChange, currentToDo }) {
  return (
    <form onSubmit={onSubmit}>
      <input type="text" value={currentToDo} onChange={onChange} required />
      <button type="submit">submit</button>
    </form>
  );
}

function ListOfToDos({ toDos }) {
  toDos.map((todo) => {
    return (
      <li key={todo}>
        {todo} <span>ùï©</span>
      </li>
    );
  });
}

export default App;
