import "./App.css";
import React, { useState } from "react";

function App() {
  const [toDo, setToDo] = useState([]);
  const [currentToDo, setCurrentToDo] = useState("");

  const handleSubmit = (e) => {
    setToDo((prev) => [...prev, currentToDo]);
    e.preventDefault();
    alert(toDo);
  };

  const handleChange = ({ target }) => {
    setCurrentToDo(() => target.value);
  };
  return (
    <>
      <UserInput onSubmit={handleSubmit} toDo={toDo} onChange={handleChange} />
      <ListOfToDos toDos={toDo} />
    </>
  );
}

function UserInput({ onSubmit, onChange }) {
  return (
    <form onSubmit={onSubmit}>
      <input type="text" onChange={onChange} required />
      <button type="submit">submit</button>
    </form>
  );
}

function ListOfToDos({ toDos }) {
  toDos.map((todo) => {
    return <p key={todo}>{todo}</p>;
  });
}

export default App;
