import "./App.css";
import React, { useState } from "react";

function App() {
  const [toDo, setToDo] = useState([]);

  const handleSubmit = (e) => {
    setToDo((prev) => [...prev, e.target.value]);
    e.preventDefault();
    alert(toDo);
  };
  return (
    <>
      <UserInput setToDo={setToDo} toDo={toDo} />
      <ListOfToDos toDos={toDo} />
    </>
  );
}

function UserInput({ setToDo }) {
  return (
    <form onSubmit={setToDo((prev) => [...prev, e.target.value])}>
      <input type="text" required />
      <button type="submit">submit</button>
    </form>
  );
}

function ListOfToDos({ toDos }) {
  toDos.map((todo) => {
    return <p key={todo}>{todo}</p>;
  });
}

export default App;
